import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import Game from './Game';
import Score from './Score';

const value1 = Math.floor(Math.random() * 100);
const value2 = Math.floor(Math.random() * 100);
const value3 = Math.floor(Math.random() * 100);
const proposedAnswer = Math.floor(Math.random() * 3) + value1 + value2 + value3;
const numQuestions = 0;
const numCorrect = 0;

class App extends Component {
  constructor(){
    super();
    this.state = {

      numQuestions:0,
      numCorrect : 0
    }
  }
  handleButtonClick = (iscorrect)=>{
    this.setState((currentState)=>({
      numQuestions : currentState.isCorrect+1,
      numCorrect : iscorrect ? currentState.numCorrect+1 : currentState.numCorrect
    }))

  }
  render() {
    return (
      <div className="App">
        <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
          <h1 className="App-title">ReactND - Coding Practice</h1>
        </header>
        <div className="game">
          <h2>Mental Math</h2>
          <div className="equation">
           <Game/>

          </div>
          <button onClick={()=>this.handleButtonClick(this.state.isCorrect)}>True</button>
          <button onClick={()=>this.handleButtonClick(this.state.isCorrect)}>False</button>
         <Score/>
        </div>
      </div>
    );
  }
}

export default App;
