import React, { Component } from "react";
import ChatTitle from "../../ChatTitle";

export default class ChatB extends Component {
  state = {
    value: "",
    data: [],
  };
  handleInput = (e) => {
    this.setState(() => ({
      value: e.target.value,
    }));
  };

  handleSubmit= (event) =>{
    alert(this.state.value)
    const { data } = this.state;
    event.preventDefault();
    if (this.state.data == this.state.value) {
      alert("product exist");
    } else {
      this.setState((currentState) => ({
        data: [...currentState.data, this.state.value],
      }));
    }
  }
  emptyFiled = () => {
    return this.state.value === "";
  };
  removeProductItem = (item) => {
    const { productItem } = this.state;
    this.setState((prevState) => ({
      productItem: prevState.productItem.filter((c) => {
        return c !== item;
      }),
    }));
  };
  render() {


    return (
      <div className="chatBox1">
        <div className="chatTitle">
          <ChatTitle />
        </div>
        <div className="messangerName">
          <h3>John</h3>
        </div>
        <div className="message">
          <ul className="msglist">
          {data.map((item) => (
              <li style={{color:'blue'}}>{"Amy:  "+item}</li>
            ))}
          </ul>
        </div>
        <div>
          {/* {(event)=>this.handleSubmit(event)} */}
          <form onSubmit={(event) => this.onHandleSubmit(event)}>
            <input
              type="text"
              name="product"
              placeholder="Say Something"
                value={this.state.value}
                onChange={this.onInputHandle}
            />

            <button>submit</button>
          </form>
        </div>
      </div>
    );
  }
}
