import React, { Component } from "react";

export default class Game extends Component {

    constructor(){
        super()
        const randomnum = this.props.randomNumber;
        this.state = {
            value1: randomnum([0]),
            value2:'',
            value3:'',
            proposedAnswer:'',

        }
    }

updateState = ()=>{
    this.setState({
        value1 : randomNumber([0]),
        value2 : randomNumber([1]),
        value3 : randomNumber([2]),
        proposedAnswer : randomNumber([3])
    })

}
sumOfValues = ()=>{

    const sum = this.state.value1+this.state.value2 +this.state.value3;
    return sum;
}



  render() {

    return (
    <div>
        <div>
        <p className="text">{this.sumOfValues()} = {this.state.proposedAnswer}</p>
        </div>
        <button onClick={this.updateState()}>True</button>
        <button onClick={(e)=>this.handleButtonClick(e)}>False</button>
    </div>
    );
  }
}
