import React, { Component } from "react";
import UsersList from './UsersList'
export default class Users extends Component {
  constructor()
  {
    super();
    this.state = {
      fname :'',
      lname :'',
      username :'',
      game : 0,
      unique : true,

    }
  }
handleUserInput = (e)=>{
  const name = e.target.name;

  const value = e.target.value;
  this.setState({
    [name] : value,
  })
}
addUsers = (e)=>{
  e.preventDefault();
  if(this.props.userList.filter((item) => item.username === this.state.username
  ).length>0)
  {
    alert('user name must be unique')
  }
  else{
    this.setState({unique:true})
    this.props.handleButton({...this.state});
  }

}
removeUser = (users)=>{
  const {userList} = this.props;
  this.setState((prevState)=>({
    userList : prevState.userList.filter(c => c.username !=users)
  }))
}
  render() {
    const { handleButton,userList} = this.props;
    return (
      <div>
        <div className="container">
          <div className="form">
            <div>
              <h2>User Game List</h2>
            </div>
            <form onSubmit={this.addUsers}>
              <div>
                <input
                name="fname"
                  type="text"
                  required
                  autoComplete="off"
                  placeholder="first name"
                    value={this.state.fname}
                    onChange={this.handleUserInput}
                />
              </div>
              <div>
                <input
                  name="lname"
                  type="text"
                  required
                  placeholder="last name"
                    value={this.state.lname}
                    onChange={this.handleUserInput}
                />
              </div>
              <div>
                <input
                name="username"
                  type="text"
                  required
                  placeholder="user name"
                    value={this.state.username}
                    onChange={this.handleUserInput}
                />
              </div>
              <div></div>
              <button>submit</button>
            </form>
          </div>

          <UsersList Userdata = {userList} isunique={this.state.unique} gameplayed={this.state.game} removeButton={this.removeUser}/>

        </div>
      </div>
    );
  }
}
