import React, { Component } from "react";
import logo from "./logo.svg";
import "./App.css";
import ChatA from "./pages/Chatbox1/ChatA";
import ChatB from "./pages/Chatbox2/ChatB";

class App extends Component {
  constructor() {
    super();
    this.state = {
      value: "",
      data: [],
    };
    // this.handleInput = this.handleInput.bind(this);
    // this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleInput = (e) => {
    this.setState(() => ({
      value: e.target.value,
    }));
  };

  handleSubmit= (event) =>{
    alert(this.state.value)
    const { data } = this.state;
    event.preventDefault();
    if (this.state.data == this.state.value) {
      alert("product exist");
    } else {
      this.setState((currentState) => ({
        data: [...currentState.data, this.state.value],
      }));
    }
  }
  emptyFiled = () => {
    return this.state.value === "";
  };
  removeProductItem = (item) => {
    const { productItem } = this.state;
    this.setState((prevState) => ({
      productItem: prevState.productItem.filter((c) => {
        return c !== item;
      }),
    }));
  };
  render() {
    return (
      <div className="App">
        <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
          <h1 className="App-title">ReactND - Coding Practice</h1>
        </header>
        <div className="ChatContainer">
          <ChatA
            value={this.state.value}
            onInputHandle={this.handleInput}
            onHandleSubmit={this.handleSubmit}
            displayMsg = {this.state.data}
          />
          <ChatB  
            displayMsg = {this.state.data}

            />
        </div>
      </div>
    );
  }
}

export default App;
