import React, { Component } from "react";
import logo from "./logo.svg";
import "./App.css";

class App extends Component {
  constructor() {
    super();
    this.state = {
      value: "",
      productItem: [],
    };
    this.handleInput = this.handleInput.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleInput = (e) => {
    this.setState(() => ({
      value: e.target.value,
    }));
  };

  handleSubmit(event) {
    event.preventDefault();

    this.setState((currentState) => ({
      productItem: [...currentState.productItem, this.state.value],
    }));
  }
  removeProductItem = (value)=>{
    const {productItem} = this.state;
    this.setState((prevState)=>({
      productItem : this.state.productItem.filter(c=>c.name!==value),
    }))
  }
  render() {
    return (
      <div className="App">
        <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
          <h1 className="App-title">ReactND - Coding Practice</h1>
        </header>
        <div className="container">
          <form onSubmit={this.handleSubmit}>
            <input
              type="text"
              name="product"
              placeholder="Say Something"
              value={this.state.value}
              onChange={(e) => this.handleInput(e)}
            />
            <button>submit</button>
          </form>
          <p className="echo">Echo:</p>
          <ol className="contact-list">
          {this.state.productItem.map((item, index) => (
            <li className="contact-list-item" key={index}>
              <div
                className="contact-avatar"

              >
                <p>{item}</p>
              </div>
              <div className="contact-details">


              </div>
              <button
                onClick={(item)=>this.removeProductItem(item)}
                className="contact-remove"
              >
                Remove
              </button>
            </li>




          ))}
        </ol>




        </div>
      </div>
    );
  }
}

export default App;
