import React, { Component } from "react";
import ChatTitle from "../../ChatTitle";

export default class ChatB extends Component {
  state = {
    value: "",
    dataB: [],
  };
  handleInput = (e) => {
    this.setState(() => ({
      value: e.target.value,
    }));
  };

  handleSubmit= (event) =>{

    const { dataB } = this.state;
    event.preventDefault();
    if (this.state.dataB == this.state.value) {
      alert("product exist");
    } else {
      this.setState((currentState) => ({
        dataB: [...currentState.dataB, this.state.value],
      }));
    }
  }
  emptyFiled = () => {
    return this.state.value === "";
  };
  removeProductItem = (item) => {
    const { data } = this.state;
    this.setState((prevState) => ({
      data: prevState.data.filter((c) => {
        return c !== item;
      }),
    }));
  };
  render() {
    const { onHandleSubmit, value, onInputHandle, displayMsg } = this.props;

    return (
      <div className="chatBox1">
        <div className="chatTitle">
          <ChatTitle />
        </div>
        <div className="messangerName">
          <h3>John</h3>
        </div>
        <div className="message">
          <ul className="msglist">
          {displayMsg.map((item) => (
              <li style={{color:'blue'}}>{"Amy:  "+item}</li>
            ))}
            {this.state.dataB.map((item) => (
              <li style={{color:'blue'}}>{"John:  "+item}</li>
            ))}
          </ul>
        </div>
        <div>
          {/* {(event)=>this.handleSubmit(event)} */}
          <form onSubmit={(event) => this.handleSubmit(event)}>
            <input
              type="text"
              name="data"
              placeholder="Say Something"
                value={this.state.value}
                onChange={this.handleInput}
            />

            <button>submit</button>
          </form>
        </div>
      </div>
    );
  }
}
