import React, { Component } from "react";
import ChatTitle from "./ChatTitle";

import ChatWindow from "./ChatWindow";
export default class inputHistory extends Component {
    constructor() {
        super();
        this.state = {
          value: "",
          valueB: "",
          data: [],
          dataB : []
        };
        // this.handleInput = this.handleInput.bind(this);
        // this.handleSubmit = this.handleSubmit.bind(this);
      }

      handleInput = (e) => {
        this.setState(() => ({
          value: e.target.value,
        }));
      };

      handleSubmit= (event) =>{
        alert(this.state.value)
        const { data } = this.state;
        event.preventDefault();
        if (this.state.data == this.state.value) {
          alert("product exist");
        } else {
          this.setState((currentState) => ({
            data: [...currentState.data, this.state.value],
          }));
        }
      }
      emptyFiled = () => {
        return this.state.value === "";
      };
      removeProductItem = (item) => {
        const { productItem } = this.state;
        this.setState((prevState) => ({
          productItem: prevState.productItem.filter((c) => {
            return c !== item;
          }),
        }));
      };

  render() {

    return (
      <div className="chatBox1">
        <div className="chatTitle">
          <ChatTitle  />
        </div>
        <div className="messangerName">
          <h3>Amy</h3>
        </div>
        <div className="message">
        <ChatWindow chatDataA={this.state.data}/>
        </div>
        <div>
          <form onSubmit={(event) => this.handleSubmit(event)}>
            <input
              type="text"
              name="data"
              placeholder="Say Something"
              value={this.state.value}
              onChange={this.handleInput}
            />

            <button>submit</button>
          </form>
        </div>
      </div>
    );
  }
}
