import React, { Component } from "react";
import ChatTitle from "../../ChatTitle";
import "./style.css";
export default class ChatA extends Component {
  render() {
    const { onHandleSubmit, value, onInputHandle, displayMsg } = this.props;
    return (
      <div className="chatBox1">
        <div className="chatTitle">
          <ChatTitle />
        </div>
        <div className="messangerName">
          <h3>Amy</h3>
        </div>
        <div className="message">
          <ul className="msglist">
            {displayMsg.map((item) => (
              <li style={{color:'lightgreen'}}>{"Amy:  "+item}</li>
            ))}
          </ul>
        </div>
        <div>
          <form onSubmit={(event) => onHandleSubmit(event)}>
            <input
              type="text"
              name="data"
              placeholder="Say Something"
              value={value}
              onChange={onInputHandle}
            />

            <button>submit</button>
          </form>
        </div>
      </div>
    );
  }
}
