import React, { Component } from "react";

export default class Game extends Component {

    constructor(){
        super()
        this.state = {
            value1:this.generateNumber[0],
            value2:this.generateNumber[1],
            value3:this.generateNumber[2],
            proposedAnswer:this.generateNumber[3],
        }
    }
  generateNumber = () => {
    const value1 = Math.floor(Math.random() * 100);
    const value2 = Math.floor(Math.random() * 100);
    const value3 = Math.floor(Math.random() * 100);
    const proposedAnswer =
      Math.floor(Math.random() * 3) + value1 + value2 + value3;
      return [value1,value2,value3,proposedAnswer];
  };

updateState = ()=>{
    this.setState((currentState)=>({
        value1 : currentState.generateNumber[0],
        value2 : currentState.generateNumber[1],
        value3 : currentState.generateNumber[2],
        answer : currentState.generateNumber[3],
    }))
}

handleButtonClick = (e)=>{
    this.updateState();
    const results = this.state.value1+this.state.value2+this.state.value3 === this.state.proposedAnswer
    const isequal =  e.target.innerText ==='false' ? false : true;
    const matchAns = results === isequal ? this.props.isCorrect+1 : this.props.isCorrect;
    return matchAns;
}
  render() {

    return (
    <div>
        <div>

        </div>
        <button onClick={(e)=>this.handleButtonClick(e)}>True</button>
          <button onClick={(e)=>this.handleButtonClick(e)}>False</button>
    </div>
    );
  }
}
