import React, { Component,useState } from "react";

const UsersList = (props)=>{
      const {isunique,Userdata,gameplayed} = props
      const [remove, setRemove] = useState('');

      const removeButton = (value)=>{
        setRemove((currentState)=>({
            Userdata : currentState.Userdata.filter(c=>c.username !==value)
        }))
      }
    return (

        <div className="table">
          <div className="heading">
            <p>First Name</p>
            <p>Last Name</p>
            <p>User Name</p>
            <p>Game</p>
            <p>Action</p>
          </div>
          { 
           (Userdata.map(user=>(
          <div className="userdata">
            <p>{user.fname}</p>
            <p>{user.lname}</p>
            <p>{user.username}</p>
            <p>{gameplayed}</p>
            <button onClick={()=>removeButton(user)}>Delete</button>
          </div>)))}



        </div>

    );
  }
export default UsersList
