import React, { Component } from "react";
import Score from './Score';
export default class Game extends Component {

    constructor(props){
        super(props)
        const randomnum = this.props.randomNumber();
        console.log(randomnum[0]);
        this.state = {
            value1: randomnum[0],
            value2:randomnum[1],
            value3:randomnum[2],
            proposedAnswer:randomnum[3],
            isCorrect : 0,
            numQuestions : 0

        }
    }
matchAnswer =  (isEqual)=>{
    alert(isEqual)
    const sumOfvalue = this.state.value1 + this.state.value1 + this.state.value1;
    const sumOfProposed = this.state.proposedAnswer;
    alert(sumOfvalue)
    alert(sumOfProposed)
    const matchAns =  sumOfvalue=== sumOfvalue;
    alert(matchAns)
    return matchAns === isEqual;
}
updateState = ()=>{
    const randomnum = this.props.randomNumber();
    this.setState({

        value1 : randomnum[0],
        value2 :randomnum[1],
        value3 :randomnum[2],
        proposedAnswer : randomnum[3],
    })

}
onButtonClick = (e)=>{
    this.updateState();
    const iscorrect = Boolean(e.target.innerText) === 'False' ? false: true;
    const score =  this.matchAnswer(iscorrect);
    return score ? this.state.isCorrect +1 : this.numQuestions+1;
}



  render() {

    return (
    <div>
        <div>
        <p className="text">{this.state.value3} + {this.state.value3}+{this.state.value3} = {this.state.proposedAnswer}</p>
        </div>
        <button onClick={(e)=>this.onButtonClick(e)}>True</button>
        <button onClick={this.onButtonClick}>False</button>

        <Score iscorrect={this.state.iscorrect} numQuestions={this.state.numQuestions}/>
    </div>
    );
  }
}
