import React, { Component } from "react";
import ChatTitle from "../../ChatTitle";

export default class ChatB extends Component {

  render() {
    const { onHandleSubmit, value, onInputHandle, displayMsg,valueB } = this.props;

    return (
      <div className="chatBox1">
        <div className="chatTitle">
          <ChatTitle/>
        </div>
        <div className="messangerName">
          <h3>John</h3>
        </div>
        <div className="message">
          <ul className="msglist">

            {this.state.value.map((item) => (
              <li style={{color:'blue'}}>{"John:  "+item}</li>
            ))}
            {displayMsg.map((item) => (
              <li style={{color:'green'}}>{"Amy:  "+item}</li>
            ))}
          </ul>
        </div>
        <div>
          {/* {(event)=>this.handleSubmit(event)} */}
          <form onSubmit={(event) => this.onHandleSubmit(event)}>
            <input
              type="text"
              name="data"
              placeholder="Say Something"
                value={valueB}
                onChange={onInputHandle}
            />

            <button>submit</button>
          </form>
        </div>
      </div>
    );
  }
}
